import React, { useState, useEffect } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu';
import Modal from 'react-modal';
import './DevisAffichage.css';

Modal.setAppElement('#root');

const DevisAffichage = () => {
  const [devis, setDevis] = useState([]);
  const [filteredDevis, setFilteredDevis] = useState([]);
  const [formData, setFormData] = useState({
    Client: '',
    TotalHT: '',
    TVA: '',
    TotalTTC: '',
    Status: '',
  });
  const [showModal, setShowModal] = useState(false);
  const [editId, setEditId] = useState(null);
  const [filterStatus, setFilterStatus] = useState('Tous');

  useEffect(() => {
    fetchDevis();
  }, []);

  async function fetchDevis() {
    const { data, error } = await supabase.from('Devis').select('*');
    if (!error) {
      setDevis(data);
      setFilteredDevis(data);
    } else {
      console.error('Erreur lors du chargement des devis', error.message);
    }
  }

  const handleEdit = (devis) => {
    setEditId(devis.id);
    setFormData({
      Client: devis.Client,
      TotalHT: devis.TotalHT.toString(),
      TVA: devis.TVA.toString(),
      TotalTTC: devis.TotalTTC.toString(),
      Status: devis.Status,
    });
    setShowModal(true);
  };

  const handleShow = (devis) => {
    setFormData({
      Client: devis.Client,
      TotalHT: devis.TotalHT.toString(),
      TVA: devis.TVA.toString(),
      TotalTTC: devis.TotalTTC.toString(),
      Status: devis.Status,
    });
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setEditId(null);
    setShowModal(false);
  };

  const handleSave = async () => {
    const { error } = await supabase
      .from('Devis')
      .update({
        Client: formData.Client,
        TotalHT: parseFloat(formData.TotalHT),
        TVA: parseFloat(formData.TVA),
        TotalTTC: parseFloat(formData.TotalTTC),
        Status: formData.Status,
      })
      .match({ id: editId });
    if (!error) {
      fetchDevis();
      handleCloseModal();
    } else {
      console.error('Erreur lors de la mise à jour', error.message);
    }
  };

  const handleDelete = async (id) => {
    const { error } = await supabase.from('Devis').delete().match({ id });
    if (!error) {
      setDevis(devis.filter(devis => devis.id !== id));
      setFilteredDevis(filteredDevis.filter(devis => devis.id !== id));
    } else {
      console.error('Erreur lors de la suppression', error.message);
    }
  };

  const handleConvertToFacture = async (id) => {
    const { data: devis, error } = await supabase.from('Devis').select('*').eq('id', id).single();
    if (error) {
      alert(`Erreur lors de la récupération du devis: ${error.message}`);
    } else {
      const { error: insertError } = await supabase.from('Facture').insert([devis]);
      if (insertError) {
        alert(`Erreur lors de la conversion en facture: ${insertError.message}`);
      } else {
        alert('Devis converti en facture avec succès!');
        fetchDevis();
      }
    }
  };

  const handleExportCSV = () => {
    const csvContent = devis.map((row) => Object.values(row).join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'devis.csv';
    link.click();
  };

  const handleSearch = (e) => {
    const searchTerm = e.target.value.toLowerCase();
    setFilteredDevis(devis.filter(devis => devis.Client.toLowerCase().includes(searchTerm)));
  };

  const handleFilterStatus = (e) => {
    const status = e.target.value;
    setFilterStatus(status);
    setFilteredDevis(
      status === 'Tous'
        ? devis
        : devis.filter((devis) => devis.Status.toLowerCase() === status.toLowerCase())
    );
  };

  return (
    <div className="appaffichage">
      <Menu />
      <div className="devis-affichage-container">
        <h1>Devis</h1>
        <div className="buttons-container">
          <input type="file" id="file-upload" />
          <label htmlFor="file-upload">Importer CSV</label>
          <button className="export-btn" onClick={handleExportCSV}>Exporter CSV</button>
        </div>
        <div className="search-container">
          <input
            type="text"
            className="search-input"
            placeholder="Rechercher un devis"
            onChange={handleSearch}
          />
          <select value={filterStatus} onChange={handleFilterStatus}>
            <option value="Tous">Tous</option>
            <option value="Accepté">Accepté</option>
            <option value="Refusé">Refusé</option>
          </select>
        </div>
        <div className="totals-container">
          <div className="total">
            <h3>Total HT</h3>
            <p>{devis.reduce((total, devis) => total + devis.TotalHT, 0).toFixed(2)} €</p>
          </div>
          <div className="total">
            <h3>Total TTC</h3>
            <p>{devis.reduce((total, devis) => total + devis.TotalTTC, 0).toFixed(2)} €</p>
          </div>
        </div>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Client</th>
                <th>Total HT</th>
                <th>TVA</th>
                <th>Total TTC</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredDevis.map((devis) => (
                <tr key={devis.id}>
                  <td>{devis.Client}</td>
                  <td>{devis.TotalHT}</td>
                  <td>{devis.TVA}</td>
                  <td>{devis.TotalTTC}</td>
                  <td className={devis.Status === 'Accepté' ? 'status-accepted' : 'status-refused'}>
                    {devis.Status}
                  </td>
                  <td>
                    <button className="action-btn edit-btn" onClick={() => handleEdit(devis)}>Modifier</button>
                    <button className="action-btn view-btn" onClick={() => handleShow(devis)}>Afficher</button>
                    <button className="action-btn delete-btn" onClick={() => handleDelete(devis.id)}>Supprimer</button>
                    <button className="action-btn save-btn" onClick={() => handleConvertToFacture(devis.id)}>Convertir en Facture</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <Modal
        isOpen={showModal}
        onRequestClose={handleCloseModal}
        contentLabel="Modal"
        className="modal"
        overlayClassName="modal-overlay"
      >
        <h2>{editId ? 'Modifier' : 'Afficher'} Devis</h2>
        <form>
          <label>
            Client:
            <input
              type="text"
              value={formData.Client}
              onChange={(e) => setFormData({ ...formData, Client: e.target.value })}
              disabled={!editId}
            />
          </label>
          <label>
            Total HT:
            <input
              type="text"
              value={formData.TotalHT}
              onChange={(e) => setFormData({ ...formData, TotalHT: e.target.value })}
              disabled={!editId}
            />
          </label>
          <label>
            TVA:
            <input
              type="text"
              value={formData.TVA}
              onChange={(e) => setFormData({ ...formData, TVA: e.target.value })}
              disabled={!editId}
            />
          </label>
          <label>
            Total TTC:
            <input
              type="text"
              value={formData.TotalTTC}
              onChange={(e) => setFormData({ ...formData, TotalTTC: e.target.value })}
              disabled={!editId}
            />
          </label>
          <label>
            Status:
            <input
              type="text"
              value={formData.Status}
              onChange={(e) => setFormData({ ...formData, Status: e.target.value })}
              disabled={!editId}
            />
          </label>
          <div className="buttons-container">
            {editId && (
              <button type="button" className="print-btn" onClick={handleSave}>Enregistrer</button>
            )}
            <button type="button" className="close-btn" onClick={handleCloseModal}>Fermer</button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default DevisAffichage;
